    }
    return(
        <div className="Counter">
        <span className="count">count</span>
        <div>
        <button
         className = "counterButton"
          onClick={incrementCounterFunction}
   //       style={buttonStyle}
          >+1</button>
        </div>
        </div>
    )
};
