import { useState } from "react"
import "./Counter.css" 
import{PropTypes} from 'prop-types'

// const buttonStyle ={
//     fontSize :"16px",
//     backgrounColor : "#00a5ab",
//     width:"50px",
//     margin:"10px",
//     color:"white",
//     padding:"15px",
//     borderRadius:"30px"
// }
export default function Counter(){
    const [count,setcount] = useState(0)
    function incrementCounterParentFunction(by){
        setcount(count+by)
    }
    function decrementCounterParentFunction(by){
        setcount(count-by)
    }
    return(
    <>
    <span className="totalCount">{count}</span>
    <CounterButton by= {1} incrementmethod = {incrementCounterParentFunction} decrementmethod= {decrementCounterParentFunction}/>
    <CounterButton by= {2} incrementmethod = {incrementCounterParentFunction} decrementmethod= {decrementCounterParentFunction}/>
    <CounterButton by= {5} incrementmethod = {incrementCounterParentFunction} decrementmethod= {decrementCounterParentFunction}/>
    </>
    )
}

 function CounterButton({by , incrementmethod,decrementmethod}){
    const [count,setcount] = useState(0) ;     //deconstructing to maintain state easily
    function incrementCounterFunction(){
        setcount(count + by)
        console.log(count)
        incrementmethod(by)
    //    console.log("increment done")
    }
    function decrementCounterFunction(){
        setcount(count -by)
        console.log(count)
        decrementmethod(by)
    //    console.log("decrement done")
    }
    return(
        <div className="Counter">
        {/* <span className="count">{count}</span> */}
        <div>
        <button
         className = "counterButton"onClick={incrementCounterFunction}//style={buttonStyle}
         >+{by}</button>

        <button className = "counterButton"onClick={decrementCounterFunction}
         >-{by}</button>
        </div>
        </div>
    )
}

CounterButton.propTypes={
    by: PropTypes.number
}

CounterButton.defaultPros = {
    by : 5
}
