import axios from "axios";

const apiClient = axios.create(
    {
    baseURL:'http://localhost:8080'
    }
);

export const retrieveHelloWorldBean= ()=>{
    return axios.get('http://localhost:8080/hello-world');
}

// export const retrieveHelloWorldPathVariable= (username)=>{
//     return axios.get(`http://localhost:8080/hello-world/path-variable/${username}`);
// }

export const retrieveAllTodosForUsernameApi = (username)=> apiClient.get(`/users/${username}/todos`);

export const deleteTodo = (username,id) =>apiClient.delete(`/users/{username}/todos/{id}`) ;
