import { useState } from "react"
import "./Counter.css" 
import{PropTypes} from 'prop-types'

export default function CounterButton({by , incrementmethod,decrementmethod}){
    const [count,setcount] = useState(0) ;     //deconstructing to maintain state easily
    function incrementCounterFunction(){
        setcount(count + by)
        console.log(count)
        incrementmethod(by)
    //    console.log("increment done")
    }
    function decrementCounterFunction(){
        setcount(count -by)
        console.log(count)
        decrementmethod(by)
    //    console.log("decrement done")
    }
    return(
        <div className="Counter">
        {/* <span className="count">{count}</span> */}
        <div>
        <button
         className = "counterButton"onClick={incrementCounterFunction}//style={buttonStyle}
         >+{by}</button>

        <button className = "counterButton"onClick={decrementCounterFunction}
         >-{by}</button>
        
        </div>
        </div>
    )
}

CounterButton.propTypes={
    by: PropTypes.number
}

CounterButton.defaultPros = {
    by : 5
}
