import { useEffect, useState } from "react";
import { retrieveAllTodosForUsernameApi } from "./api/HelloWorldApiService";

export default function ListTodosComponent() {
    const today = new Date();
    const targetDate = new Date(
      today.getFullYear() + 12,
      today.getMonth(),
      today.getDay()
    );

    const [todos, setTodos] = useState([])
    // const todos = [
    //   { id: 1, description: "Learn AWS", done: false, targetDate: targetDate },
    //   {
    //     id: 2,
    //     description: "Learn Full stack dev",
    //     done: false,
    //     targetDate: targetDate,
    //   },
    //   {
    //     id: 3,
    //     description: "Learn React and spring boot",
    //     done: false,
    //     targetDate: targetDate,
    //   },
    // ];
          useEffect( () =>refreshTodos(),[] )

    function refreshTodos(){
    retrieveAllTodosForUsernameApi('manish')
    .then((response)=>{
      console.log(response.data)
      setTodos(response.data)
    }
    )
    .catch(error =>console.log(error))
    }


    return (
      <div className="container">
        <h2>Things you want to do</h2>
        <div>
          <table className="table">
            <thead>
              <tr>
                <td>ID</td>
                <td>Description</td>
                <td>Done?</td>
                <td>Target Date</td>
              </tr>
            </thead>
  
            <tbody>
              {todos.map((todo) => (
                <tr key={todo.id}>
                  <td>{todo.id+1}</td>
                  <td>{todo.description}</td>
                  <td>{todo.done}</td>
                  <td>{todo.targetDate.toString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
